mapper.select:
SELECT number, product_name, order_date, user_number FROM( SELECT number, product_name, order_date, user_number, ROW_NUMBER() OVER(PARTITION BY user_number ORDER BY order_date DESC) AS lastOrder FROM idus_order
WHERE user_number IN
#{userNumber}
) AS lastOrders WHERE lastOrder = 1;

SELECT COUNT(number)
FROM idus_order
WHERE user_number = #{userNumber}
